package service

import (
	"context"
	"net/http"
	v1 "vmapp/api/app0/v1"
	"vmapp/app0/internal/biz/usecase"
	"vmapp/app0/internal/conf"
	"vmapp/pkg/verror"

	"github.com/gogf/gf/i18n/gi18n"
	"github.com/gogf/gf/v2/frame/g"
	"github.com/gogf/gf/v2/os/gctx"
)

type GfUserService struct {
	c  *conf.AppConf
	uc *usecase.UserCase
	bc *conf.BootComponent
}

func NewGfUserService(c *conf.AppConf, bc *conf.BootComponent, uc *usecase.UserCase) *GfUserService {
	return &GfUserService{
		c:  c,
		uc: uc,
		bc: bc,
	}
}

func (t *GfUserService) GetHello(ctx context.Context, req *v1.GetelloReq) (res *v1.GetHelloRes, err error) {

	r := g.RequestFromCtx(ctx)
	v, err := r.Session.Get("user_id")
	if err != nil {
		return nil, err
	}

	if err := g.Validator().Data(req).I18n(g.I18n("zh-CN")).Run(ctx); err != nil {

		g.Dump(err.String())
	}

	res = &v1.GetHelloRes{
		Content: v.String() + "",
	}
	return
}

func (t *GfUserService) Hello(ctx context.Context, req *v1.HelloReq) (res *v1.HelloRes, err error) {
	i18n := gi18n.New()
	i18n.Translate(ctx, `hello`)

	t.uc.GetHello(ctx)

	id := gctx.CtxId(ctx)
	panic("hello")
	return nil, verror.NewError(http.StatusInternalServerError, id)
	r := g.RequestFromCtx(ctx)
	r.Session.Set("user_id", 1)

	if err := g.Validator().Data(req).I18n(g.I18n("zh-CN")).Run(ctx); err != nil {

		g.Dump(err.String())
	}

	res = &v1.HelloRes{
		Content: "hello world",
	}
	return
}
